import React from "react";

function Card(props) {
  const cardImage = props.card.link;
  const cardName = props.card.name;
  const likes = React.useState(0);

  function handleClick() {
    props.onCardClick(props.card.img);
  }

  return (
    <li className="cards__list-item">
      <div className="card" onClick={handleClick}>
        <button className="card__remove-button" type="button"></button>
        <img className="card__image" alt={cardName} src={cardImage} />
        <div className="card__description">
          <h2 className="card__title">{cardName}</h2>
          <div className="card__like-container">
            <button type="button" className="card__like-button"></button>
            <p className="card__like-counter">{likes}</p>
          </div>
        </div>
      </div>
    </li>
  );
}

export default Card;
