// import {
//     popupClassList as popupClasses,
//     formClassList as formClasses

import {selectors} from "../utils/constants";



function PopupWithForm(props) {
    const sectionClassName = `${selectors.popup} ${"popup_type_"}${props.name} ${props.isOpen ? selectors.openedPopup : ""}`;

  return (
    <section className={`popup popup_type_${props.name}`}>
      <div className="popup__container">
        <button
          type="button"
          className={`popup__close-button popup__close-button_${props.name}`}
        ></button>
        <h2 className="popup__title">{props.title}</h2>
        <form
          novalidate
          className={`form form_type_${props.name}`}
          id="form"
          name={props.name}
        >
          <input
            className="form__input"
            placeholder="Title"
            type="text"
            id="image-title"
            name="name"
            required
            minlength="1"
            maxlength="30"
          />
          <span id="image-title-error" className="form__error"></span>
          <input
            className="form__input"
            placeholder="Image link"
            type="url"
            id="image-url"
            name="link"
            required
          />
          <span id="image-url-error" className="form__error"></span>
          <button type="submit" className="form__submit form__submit_disabled">
            Create
          </button>
        </form>
      </div>
    </section>
  );
}

export default PopupWithForm;
