import React from "react";
import api from "../utils/api";

// import Card from "./Card";

function Main(props) {
  
  const [userName, setUserName] = React.useState("Jacques Cousteau");
  const [userDescription, setUserDescription] = React.useState("Explorer");
  const [userAvatar, setUserAvatar] = React.useState("");
  const [cards, setCards] = React.useState([]);


  
    React.useEffect(() => {
      api
        .getRemoteData()
        .then(([userData, initialCards]) => {
          setUserName(userData.name);
          setUserDescription(userData.about);
          setUserAvatar(userData.avatar);
          setCards([...initialCards]);
        })
        .catch(console.log);
    }, []);

  return (
    <main className="main">
      <section className="profile">
        <div className="profile__info">
          <div className="profile__img-container">
            <img
              id="profileImg"
              className="profile__img"
              alt="travaler profile"
              src={userAvatar}
            />
            <button type="button" className="profile__img-edit-button" onClick={props.onEditAvatarClick}></button>
          </div>
          <div className="profile__details">
            <div className="profile__details-top">
              <h1 className="profile__name">{userName}</h1>
              <button type="button" className="profile__edit-button" onClick={props.onEditProfileClick}></button>
            </div>
            <p className="profile__title">{userDescription}</p>
          </div>
        </div>

        <button type="button" className="profile__add-button" onClick={props.onAddPlaceClick}></button>
      </section>
      <section className="cards">
        <ul className="cards__list"></ul>
      </section>
    </main>
  );
}

export default Main;
